package hp.com.controller;import hp.com.dto.request.RequestRegisterUser;import hp.com.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/user")public class UserController extends AbstractController{    @Autowired    private UserService service;    @PostMapping("/add")    public ResponseEntity<?> register(@RequestBody RequestRegisterUser newUser) {        return response(service.register(newUser));    }}