package hp.com.log;import org.apache.log4j.LogManager;import org.apache.log4j.Logger;public class LOG {    public static Logger LOG_INFO_FILE = LogManager.getLogger("fout");    public static Logger LOG_INFO_CONSOLE = LogManager.getLogger("console");    public static void infoFile(String s) {        LOG_INFO_FILE.info(s);    }    public static void infoConsole(String s) {        LOG_INFO_CONSOLE.info(s);    }    public static void errorFile(String s) {        LOG_INFO_FILE.error(s);    }    public static void main(String[] args) {        infoConsole("abc");        infoFile("xxy");    }}